// ----------------- 从纹理表取颜色（u,v 取 0..15） -----------------
static inline uint16_t get_texture(uint8_t tex_idx, int u, int v) {
  u &= 15; v &= 15;  // 保证 u,v 在 0..15
  return TEXTURE[tex_idx][v * 16 + u];
}

